mapper.select:
    	SELECT
    		category_id AS categoryId
		FROM
			os_category
		WHERE
			status = #{status}
		AND parent_id IN (
			SELECT
				category_id AS categoryId
			FROM
				os_category
			WHERE
				STATUS = #{status}
			AND parent_id = #{categoryId}
		)
		OR parent_id = #{categoryId}
		OR category_id = #{categoryId}

    	SELECT
    		category_id AS categoryId,
			name
		FROM
			os_category
		WHERE
			status = #{status}
		AND show_in_nav = #{showInNav}
		AND type = #{type}
		ORDER BY
			sort
		LIMIT ${navNumber}
