mapper.select:
SELECT ACCOUNT_ID AS accountId, USER_NAME AS userName, REAL_NAME AS realName, SEX, AGE, PIC_IMG AS picImg, STATUS, LAST_LOGIN_TIME AS lastLoginTime, LAST_LOGIN_IP AS lastLoginIp, EMAIL, TELEPHONE, CREATE_TIME AS createTime, CREATE_BY AS createBy, UPDATE_TIME AS updateTime, UPDATE_BY AS updateBy FROM tb_system_user
AND (USER_NAME LIKE '%' #{searchContent} '%' OR REAL_NAME LIKE '%' #{searchContent} '%' OR AGE LIKE '%' #{searchContent} '%' OR LAST_LOGIN_IP LIKE '%' #{searchContent} '%' OR EMAIL LIKE '%' #{searchContent} '%' OR TELEPHONE LIKE '%' #{searchContent} '%')
AND CREATE_TIME >= #{beginCreateTime}
AND CREATE_TIME <= #{endCreateTime}
